  return (
    <div class="home-page">
      <h1>Home Page</h1>
      <div class='addNote'><NoteForm onAddNote={handleAddNote} /></div>
      <div class="latest-notes-container">
        <p>hi</p>
        <TilesContainer />
      </div>
    </div>
  );
};

export default HomePage;
