import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import TilesContainer from './LastThreeNotes.js'

const NoteForm = ({ fetchTilesData }) => {
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      console.log('hi');
        setTitle('');
        console.log("ooo");
        setContent('');
        fetchTilesData();

      const response = await fetch('http://localhost:3002/api/notes', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ title, content }),
      });
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <hi className='add'>Add New Note</hi>
      <label>
        Title:
        <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} />
      </label>
      <label>
        Content:
        <textarea value={content} onChange={(e) => setContent(e.target.value)} />
      </label>
      <button type="submit">Save<TilesContainer /></button>
    </form>
  );
};

export default NoteForm;
