import React, { useState, useEffect } from 'react';
import NoteForm from './NoteForm';
//import TileComponent from './TileComponent';
import './home.css'

const HomePage = () => {
  const [notes, setNotes] = useState([]);

  useEffect(() => {
    // Fetch the three latest notes from your backend or use your data source
    // For demonstration, using dummy data here
    const fetchLatestNotes = async () => {
      // Replace the following with your actual API call
      const response = await fetch('http://localhost:3001/three');
      const data = await response.json();

      setNotes(data.slice(0, 3)); // Display the three latest notes
    };

    fetchLatestNotes();
  }, []);

  const handleAddNote = (newNote) => {
    // Handle adding a new note to the state or send it to your backend
    // For demonstration, updating the state directly
    setNotes([newNote, ...notes]);
  };

  return (
    <div class="home-page">
      <h1>Home Page</h1>
      <div ><NoteForm onAddNote={handleAddNote} /></div>
      <div class="latest-notes-container">
        <p>hi</p>
      </div>
    </div>
  );
};

export default HomePage;
