import React, { useState, useEffect } from 'react';
import NoteForm from './NoteForm';
import TileComponent from './TileComponent';

const HomePage = () => {
  const [notes, setNotes] = useState([]);

  useEffect(() => {
    // Fetch the three latest notes from your backend or use your data source
    // For demonstration, using dummy data here
    const fetchLatestNotes = async () => {
      // Replace the following with your actual API call
      const response = await fetch('your-api-endpoint-for-latest-notes');
      const data = await response.json();

      setNotes(data.slice(0, 3)); // Display the three latest notes
    };

    fetchLatestNotes();
  }, []);

  const handleAddNote = (newNote) => {
    // Handle adding a new note to the state or send it to your backend
    // For demonstration, updating the state directly
    setNotes([newNote, ...notes]);
  };

  return (
    <div className="home-page">
      <h1>Home Page</h1>

      {/* Add New Note Section */}
      <NoteForm onAddNote={handleAddNote} />

      {/* Display Three Latest Notes */}
      <div className="latest-notes">
        {notes.map((note) => (
          <TileComponent
            key={note.id} // Replace with your note's unique identifier
            title={note.title}
            content={note.content}
            createdAt={note.createdAt}
            onClick={() => handleTileClick(note.id)}
          />
        ))}
      </div>
    </div>
  );
};

export default HomePage;
