import './LastThreeNotes.css';
import React, { useState, useEffect } from 'react';

const TilesContainer = () => {
  const [todos, setTodos] = useState([]);
  const [modalOpen, setModalOpen] = useState(false);
  const [selectedTodo, setSelectedTodo] = useState(null);


  useEffect(() => {
      const fetchTodos = async () => {
          try {
              const response = await fetch('http://localhost:3002/three');
              const todosData = await response.json();

              const formattedTodos = todosData.map(todo => ({
                  ...todo,
                  createdAt: new Date(todo.createdAt),
                  updatedAt: new Date(todo.updatedAt),
              }));

              const recentThreeTodos = formattedTodos.slice(0, 3);
              console.log(recentThreeTodos);
              setTodos(recentThreeTodos);
          } catch (error) {
              console.error(error);
          }
      };
      fetchTodos();
  }, []);
  return (
          <div class="container">
              {todos.map(todo => (
                  <div key={todo._id} class="tile" onClick={() => openModel(todo)}>
                      <h2 class="title">{todo.title}</h2>
                      <p class="content">{todo.content}</p>
                      <p class="updt">Last Updated At: {new Date(todo.updatedAt).toLocaleString()}</p>
                  </div>
              ))}
              <EditModel isOpen={modelOpen} onClose={closeModel} todo={selectedTodo} />
          </div>
  );
}

export default TilesContainer;
