import React, { useState, useEffect } from 'react';
import './NoteList.css';
const NoteList = () => {
    const [todos, setTodos] = useState([]);

    useEffect(() => {
        const fetchTodos = async () => {
            try {
                const response = await fetch('http://localhost:3000/');
                const todosData = await response.json();

                console.log('Fetched data:', todosData);

                const formattedTodos = todosData.map(todo => ({
                    ...todo,
                    createdAt: new Date(todo.createdAt),
                    updatedAt: new Date(todo.updatedAt),
                }));

                setTodos(formattedTodos);
            } catch (error) {
                console.error(error);
            }
        };
        fetchTodos();
        const intervalId = setInterval(fetchTodos, 5000);
        return () => clearInterval(intervalId);
    }, []);

    return (
        <div className="container">
            <h1>Todo List</h1>
            <div id="todos-container">
            <h2>title</h2>
                        <p>content</p>
                        <p>Updated At</p>
                        <p>Created At</p>
                {todos.map(todo => (
                    <div key={todo._id} className="todo-tile">
                        <h2>{todo.title}</h2>
                        <p>{todo.content}</p>
                        <p>Updated At: {new Date(todo.updatedAt).toLocaleString()}</p>
                        <p>Created At: {new Date(todo.createdAt).toLocaleString()}</p>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default NoteList;
